var assert = require('assert')
  , crypto = require('crypto')

var cbs = require('./');

var buffers = [
  crypto.randomBytes(16),
  Buffer('carlos'),
  Buffer('KZ1CaqEb/4iThje3sgQ59Q==', 'base64')
];

var result = cbs.serialize(buffers);

assert(Buffer.isBuffer(result));

var orig = cbs.unserialize(result);

assert.deepEqual(orig, buffers);

console.log("it's fine");
